import { useParams } from 'react-router-dom';
import { useState,useEffect } from 'react';
import axios from 'axios';
import style from '../card/card.module.css'


const Detail = ()=>{
   const [ character,setCharacter ]= useState({});

   useEffect(() => {
      axios(`https://rickandmortyapi.com/api/character/${params.id}`).then(({ data }) => {
         if (data.name) {
            setCharacter(data);
         } else {
            window.alert('No hay personajes con ese ID');
         }
      });
      return setCharacter({});
   }, [params.id]);


   const params = useParams()
   return(
      <div className={style.cards}>{
         character.map(({id,name,species,gender,image,origin,status})=>{
               return (
                  <div className={style.card}>

            <button onClick={()=>onClose(id)} className={style.buttonx}>X</button>
            <br></br>
            <br></br>
            <img src={image} alt='Img' />
            <h2>
            <NavLink to={`/detail/${id}`}>Nombre: {name}</NavLink>
            </h2>
            <h2>Especie: {species}</h2>
            <h2>Genero: {gender}</h2>
            <h2>Origen: {origin}</h2>
            <h2>Status: {status}</h2>

      </div>
               )
            }
         )
      }
   </div>
   )
}

export default Detail
