const Validation = (userData) => {

        const newErrors = {
          email: '',
          password: '',
        };

        if (!/^\S{1,35}@\S+\.\S+$/.test(form.email)) {
          newErrors.email = 'Ingresa un eMail valido';
        }

        if (!form.email) {
            newErrors.email = '';
        }

        if (form.password==='') {
            newErrors.password = '';
        }

        if (!/^(?=.*[A-Z])(?=.*\d).{5,}$/.test(form.password)) {
          newErrors.password = 'debe tener al menos seis caracteres, un numero y una mayuscula';
        }

        return newErrors;
      };

export default Validation;
