import React, { useState } from 'react';
import style from './card.module.css';

const Card = (props) => {
  const [loaded, setLoaded] = useState(false);

  const handleImageLoad = () => {
    setLoaded(true);
  };

  return (
    <div className={style.card}>
      <img
        className={style.image}
        src={props.image}
        alt={props.name}
        onLoad={handleImageLoad} // Agregar el evento onLoad
      />
      {!loaded && <div className={style.loader}></div>} {/* Agregar la animaci√≥n de carga */}
      <h2>{props.name}</h2>
      <p>{props.status}</p>
      <p>{props.species}</p>
      <p>{props.gender}</p>
    </div>
  );
};

export default Card;
