import style from './App.module.css';
import Cards from './components/cards/Cards.jsx';
import Nav from './components/nav/Nav.jsx';
import { useState } from 'react';
import axios from 'axios';
import { Routes,Route,BrowserRouter } from 'react-router-dom';



function App() {

   const [ characters,setCharacters ]= useState([]);
   const onSearch = (id)=> {
      axios(`https://rickandmortyapi.com/api/character/${id}`).then(({ data }) => {
         if (data.name) {
            setCharacters((oldChars) => [...oldChars, data]);
         } else {
            window.alert('¡No hay personajes con este ID!');
         }
      });
   }

   const onClose= (id)=>{
      const fliteredChars= characters.filter(character => character.id!== parseInt(id))
      setCharacters(fliteredChars)
   }

   return 
      <BrowserRouter className={style.App}>
         <Nav onSearch={onSearch}/>
         <Routes>
            <Route path="/cards" element= {<Cards characters={characters} onClose={onClose}/>}/>
         </Routes>
      </BrowserRouter>
   ;
}

export default App;
